import { bees } from '@beehive/stores';
import { derived } from 'svelte/store';

export default function getBee(id) {
	return derived(bees, $bees => {
		if ($bees === null || $bees === false) {
			return $bees;
		}

		for (let i = 0; i < $bees.length; i++) {
			if ($bees[i].id === id) {
				return $bees[i];
			}
		}

		return false; // Not Found
	}, null);
}

