import { chains } from '@beehive/stores';
import { derived } from 'svelte/store';

export default function getChain(id) {
	return derived(chains, $chains => {
		if ($chains === null || $chains === false) {
			return $chains;
		}

		for (let i = 0; i < $chains.length; i++) {
			if ($chains[i].id === id) {
				return $chains[i];
			}
		}

		return false; // Not Found
	}, null);
}

