import { derived } from 'svelte/store';
import { hives } from '@beehive/stores';

export default function getHive(id) {
	return derived(hives, $hives => {
		if ($hives === null || $hives === false) {
			return $hives;
		}

		for (let i = 0; i < $hives.length; i++) {
			if ($hives[i].id === id) {
				return $hives[i];
			}
		}

		return false; // Not Found
	}, null);
}

